import ReactDOM from 'react-dom';
import React, { useState, useEffect} from "react";
import { useHistory, Link} from "react-router-dom";
import NavBar from '../NavBar';
import logo from '../monkey.png';
import '../App.css'
import Modal from 'react-bootstrap/Modal'


function Mechanikai() {

    const history = useHistory();
    const token = localStorage.getItem('auth_token')
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    if(token == null){
        history.push('/prisijungimas')
    }

    const [state, setData] = useState({
        mechanikai: null
    });

    const fetchData = async () => {
        const api = await fetch("https://autoteksaitynai.azurewebsites.net/api/mechanikai",{
            method: 'get',
            headers: new Headers({
                'Authorization': 'Bearer' + token
            })
        });
        setData({
            mechanikai: await api.json(),
        });
    };

    const routeToMechanik = (id) => {
        return '/mechanikai/' + id
    }
    useEffect(() => {
        fetchData();
        }, []);


    return (
        <>
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{username}</Modal.Title>
        </Modal.Header>
        <Modal.Body><div>
            El. paštas: {usermail}
            </div>
            {role === "1"
            ? <div>Role: Administratorius</div>
            :   <div>Role: Narys</div>
            }
            </Modal.Body>
        <Modal.Footer>
          <button className='btn btn-danger' onClick={handleClose}>
            Close
          </button>
        </Modal.Footer>
      </Modal>


        <div className="sb-nav-fixed">
            <NavBar />
        <div className="container">

            <div className="row justify-content-center">
                <div className="col-md-8">
                <img src={logo} className='fotke'></img>
                    <div className="card">
                        <div className="card-header">Mechanikai</div>
                        <div><div className="card-header">Gidai <button className='btn btn-dark' onClick={handleShow}>
        Mano profilis
      </button></div>
                        <table className="table">
                        <thead>
                        <tr>
                                    <th scope="col">Vardas</th>
                                    <th scope="col">Rolė</th>
                                    <th scope="col"></th>
                                </tr>
                                </thead>


                            <tbody>
                              {
                                state?.mechanikai?.map(data => (

                                    <tr key={data?.id}>

                                    <td>{data?.name}</td>
                                        {
                                            data?.administratorius === 0
                                            ? <td>Narys</td>
                                            : <td>Administratorius</td>
                                        }
                                        <td ><Link to={routeToMechanik(data?.id)}><button className='btn btn-dark'>Profilis</button></Link></td>

                                </tr>

                                ))
                            }
                            </tbody>
                            </table>
                    </div>
                </div>
            </div>
        </div>
        </div>
        </div>
        </>
    );
}

export default Mechanikai;

if (document.getElementById('mechanikai')) {
    ReactDOM.render(<Mechanikai />, document.getElementById('mechanikai'));
}
