import ReactDOM from 'react-dom';
import React, { useEffect, useState} from "react";
import { useHistory} from "react-router-dom";
import NavBar from '../NavBar';
import { useParams } from "react-router";
import '../App.css'
import axios from 'axios';
import { DropDownList } from "@progress/kendo-react-dropdowns";

function Gidas(){
    const history = useHistory();
    const { id } = useParams();
    const token =  localStorage.getItem('auth_token')
    const [tempName, setTempName] = useState('');
    const [password, setPassword] = useState('');

    const [state, setData] = useState({
        gidai: null
    });

    const [simptomas, setSimptomai] = useState({
        simptomai: null
    });

    const [problema, setProblemos] = useState({
        problemos: null
    });

    const [mechanikai, setMechanikai] = useState({
        mechanikai: []
    });

    const [automobilis, setAuto] = useState({
        automobiliai: null
    });

    const fetchData = async () => {
        const api = await fetch("https://autoteksaitynai.azurewebsites.net/api/gidai/"+id,{
            method: 'get',
            headers: new Headers({
                'Authorization': 'Bearer' + token
            })
        });
        setData({
            gidai: await api.json(),
        });

        const apia = await fetch("https://autoteksaitynai.azurewebsites.net/api/automobiliai",{
            method: 'get',
            headers: new Headers({
                'Authorization': 'Bearer' + token
            })
        });
        setAuto({
            automobiliai: await apia.json(),
        });

        const apis = await fetch("https://autoteksaitynai.azurewebsites.net/api/simptomai",{
            method: 'get',
            headers: new Headers({
                'Authorization': 'Bearer' + token
            })
        });
        setSimptomai({
            simptomai: await apis.json(),
        });

        const apip = await fetch("https://autoteksaitynai.azurewebsites.net/api/problemos",{
            method: 'get',
            headers: new Headers({
                'Authorization': 'Bearer' + token
            })
        });
        setProblemos({
            problemos: await apip.json(),
        });

        const apim = await fetch("https://autoteksaitynai.azurewebsites.net/api/mechanikai",{
            method: 'get',
            headers: new Headers({
                'Authorization': 'Bearer' + token
            })
        });
        setMechanikai({
            mechanikai: await apim.json(),
        });
    };

    const findMechanikas = (id) => {
        var x = null;
        if(mechanikai?.mechanikai != null){
            mechanikai?.mechanikai.forEach(element => {
                if(element.id === id){
                    setTempName(x);
                }
            });
        }
    }

    const findAuto = (id) => {
        var x = null;
        if(automobilis?.automobiliai != null){
            automobilis?.automobiliai.forEach(element => {
                if(element.id === id){
                    x = element.pavadinimas + " " + element.marke;
                    if(element.kuras === "dyzelinas"){
                        x += " (d)"
                    }
                    else if(element.kuras === "benzinas"){
                        x += " (b)"
                    }
                    else {
                        x += " (e)"
                    }
                }
            });
            return x;
        }
        else{
            return 'id:' + id;
        }
    }



    useEffect(() => {
        fetchData();
        }, []);

    if(state?.gidai?.status === "Token is Expired"){
        localStorage.removeItem(`auth_token`)
        localStorage.removeItem(`user_name`)
        localStorage.removeItem(`user_role`)
        localStorage.removeItem(`user_email`)
        history.push('/prisijungimas')
        return null;
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const user = {tempName, password};
        axios.post(`https://autotek.azurewebsites.net/api/prisijungimas`, user).then(res => {
            console.log(res.data.token)
            localStorage.setItem(`auth_token`, res.data[0][0].token)
            localStorage.setItem(`user_name`, res.data[0][1][0].name)
            localStorage.setItem(`user_role`, res.data[0][1][0].administratorius)
            localStorage.setItem(`user_email`, res.data[0][1][0].email)
            history.push('/gidai')
         });

    }

    return (
        <div>
            <NavBar />
        <div className="container">
            <div className="row justify-content-center">
                <div className="col-md-8">
                    <div className="card">
                        <div className="card-header">Redaktorius</div>

                        <div className="card-body">
                        {
                            state?.gidai?.map(data => (
                        <form onSubmit={handleSubmit} key={gidas?.id}>


                        <div className="row mb-3">
                            <label htmlFor="name" className="col-md-4 col-form-label text-md-right">Autorius</label>

                            <div className="col-md-6">
                                {findMechanikas()}
                            <DropDownList
                                    data={mechanikai?.mechanikai}
                                    textField="name"
                                    dataItemKey="id"
                                    value={tempName.value}
                                    onChange={(e) => setTempName(e)}
                                />
                            </div>
                        </div>
                        <div className="row mb-0">
                            <div className="col-md-8 offset-md-4">
                                <button type="submit" className="btn btn-primary">
                                    Patvirtinti
                                </button>

                            </div>
                        </div>
                    </form>
                            ))}
                </div>
            </div>
        </div>
        </div>
        </div>
        </div>
    );
}

export default Gidas;
